// new demo
function drawTriangle(context, point1, point2, point3) {
    context.beginPath();
    context.moveTo(point1.x, point1.y);
    context.lineTo(point2.x, point2.y);
    context.lineTo(point3.x, point3.y);
    context.closePath();

    context.fillStyle = "#FFF1D6";
    context.fill();
    context.shadowBlur = 0;
    context.shadowOffsetX = -3;
    context.shadowColor = "#F7CA63";
}

var canvas = document.querySelector('#my-canvas');
var context = canvas.getContext('2d');

const point1 = {x: 2,  y: 190};
const point2 = {x: 380, y: 190};
const point3 = {x: 150, y: 180 };
drawTriangle(context, point1, point2, point3);

const drawTriangleChart = (a = 2, b = 0) => {
    const point1 = {x: 2,  y: 190};
    const point2 = {x: 380, y: 190};
    const point3 = {x: a, y: b };
     
    drawTriangle(context, point1, point2, point3);        
}
drawTriangleChart();

